typedef enum {
    Scene_MainMenu,
    Scene_PopupOne,
    Scene_RemotePanel,
    Scene_EditPanel,
    Scene_RecordPanel,
    Scene_count
} Scene;

typedef enum {
    FView_Menu,
    FView_Popup,
    FView_ButtonPanel
} FView;

typedef enum {
    Button_VolumeUp,
    Button_VolumeDown,
    Button_NavigateLeft,
    Button_NavigateUp,
    Button_NavigateRight,
    Button_NavigateDown,
    Button_Power
} Button;

typedef struct {
    uint32_t frequency;
    float duty_cycle;
    uint32_t data;
    uint32_t size;
} RawSignal;

typedef struct {
    bool isRaw;
    union {
        InfraredMessage message;
        RawSignal raw;
    } payload;
} Signal;
typedef struct {
    Signal* VolumeUp;
    Signal* VolumeDown;
    Signal* NavigateLeft;
    Signal* NavigateUp;
    Signal* NavigateRight;
    Signal* NavigateDown;
    Signal* Power;
    Signal* length;
} Messages;
//define an array so they would be mapped
typedef struct {
    SceneManager* scene_manager;
    ViewDispatcher* view_dispatcher;
    Submenu* submenu;
    Popup* popup;
    ButtonPanel* buttonPanel;
    Storage* storage;
    FlipperFormat* flipperFormat;
    Messages* messages;
    Signal* current;
} FancyRemote;

typedef enum {
    Event_ShowPopupOne,
    Event_ShowRemotePanel,
    Event_ShowEditPanel,
    Event_ShowRecordPanel
} Event;
typedef enum {
    MenuSelection_One,
    MenuSelection_Two,
    MenuSelection_Three
} MenuSelection;

const char* fancy_remote_Volume_up = "Volume_up";
const char* fancy_remote_Volume_down = "Volume_down";

uint32_t getFrequency(void* context) {
    FancyRemote* app = context;
    uint32_t num;
    flipper_format_read_uint32(app->flipperFormat, "frequency", &num, 1);
    return num;
}
float getDutyCycle(void* context) {
    FancyRemote* app = context;
    float num;
    flipper_format_read_float(app->flipperFormat, "duty_cycle", &num, 1);
    return num;
}
uint32_t getTimingsSize(void* context) {
    FancyRemote* app = context;
    uint32_t size;
    flipper_format_get_value_count(app->flipperFormat, "data", &size);
    return size;
}
uint32_t* getTimings(void* context, uint32_t size) {
    FancyRemote* app = context;
    if(size <= 1024) {
        uint32_t* data = malloc(sizeof(uint32_t) * size);
        flipper_format_read_uint32(app->flipperFormat, "data", data, size);
        return data;
    }
    return NULL;
}
Signal* indexToMessage(void* context, int index) {
    FancyRemote* app = context;
    switch(index) {
    case Button_NavigateDown:
        return app->messages->NavigateDown;
        break;
    case Button_NavigateLeft:
        return app->messages->NavigateLeft;
        break;
    case Button_NavigateRight:
        return app->messages->NavigateRight;
        break;
    case Button_NavigateUp:
        return app->messages->NavigateUp;
        break;
    case Button_Power:
        return app->messages->Power;
        break;
    case Button_VolumeDown:
        return app->messages->VolumeDown;
        break;
    case Button_VolumeUp:
        return app->messages->VolumeUp;
        break;
    default:
        return NULL;
        break;
    }
}
void setMessage(void* context, int index, Signal* signal) {
    FancyRemote* app = context;
    switch(index) {
    case Button_NavigateDown:
        app->messages->NavigateDown = signal;
        break;
    case Button_NavigateLeft:
        app->messages->NavigateLeft = signal;
        break;
    case Button_NavigateRight:
        app->messages->NavigateRight = signal;
        break;
    case Button_NavigateUp:
        app->messages->NavigateUp = signal;
        break;
    case Button_Power:
        app->messages->Power = signal;
        break;
    case Button_VolumeDown:
        app->messages->VolumeDown = signal;
        break;
    case Button_VolumeUp:
        app->messages->VolumeUp = signal;
        break;
    }
}
void makeButton(void* context, int index) {
    FancyRemote* app = context;
    FuriString* o = furi_string_alloc();
    flipper_format_read_string(app->flipperFormat, "type", o);
    Signal* signal = malloc(sizeof(Signal));
    FlipperFormat* ff = app->flipperFormat;
    const char* name = furi_string_get_cstr(o);
    furi_string_free(o);
    if(strcmp(name, "parsed") == 0) {
        signal->isRaw = false;
        InfraredMessage message = signal->payload.message;
        FuriString* buf = furi_string_alloc();
        flipper_format_read_string(ff, "protocol", buf);
        message.protocol = infrared_get_protocol_by_name(furi_string_get_cstr(buf));
        furi_string_free(buf);
        flipper_format_read_hex(ff, "address", (uint8_t*)&message.address, 4);
        flipper_format_read_hex(ff, "command", (uint8_t*)message.command, 4);
        message.repeat = true;
    } else if(strcmp(name, "raw") == 0) {
        signal->isRaw = true;
        signal->payload.raw.frequency = getFrequency(context);
        signal->payload.raw.duty_cycle = getDutyCycle(context);
        signal->payload.raw.size = getTimingsSize(context);
        signal->payload.raw.size = (uint32_t)getTimings(context, signal->payload.raw.size);
    }
    setMessage(context, index, signal);
}
void processButton(void* context, FuriString* nameIn) {
    const char* name = furi_string_get_cstr(nameIn);
    if(strcmp(name, fancy_remote_Volume_up) == 0) {
        makeButton(context, Button_VolumeUp);
    } else if(strcmp(name, fancy_remote_Volume_down) == 0) {
        makeButton(context, Button_VolumeDown);
    }
}

void irDataInit(void* context) {
    FancyRemote* app = context;
    FuriString* o = furi_string_alloc();
    if(flipper_format_file_open_existing(app->flipperFormat, EXT_PATH("infrared/Cable.ir"))) {
        while(flipper_format_read_string(app->flipperFormat, "name", o)) {
            processButton(context, o);
        }
    }
    furi_string_free(o);
    flipper_format_file_close(app->flipperFormat);
}

void sendIrSignal(void* context, int index) {
    Signal* signal = indexToMessage(context, index);
    bool test = signal->isRaw;
    if(test) {
        infrared_send_raw_ext(
            &signal->payload.raw.data,
            signal->payload.raw.size,
            true,
            signal->payload.raw.frequency,
            signal->payload.raw.duty_cycle);
    } else {
        const InfraredMessage* message = &signal->payload.message;
        infrared_send(message, 10);
    }
}

void fancy_remote_menu_callback_main_menu(void* context, uint32_t index) {
    FancyRemote* app = context;
    switch(index) {
    case MenuSelection_One:
        scene_manager_handle_custom_event(app->scene_manager, Event_ShowPopupOne);
        break;
    case MenuSelection_Two:
        scene_manager_handle_custom_event(app->scene_manager, Event_ShowRemotePanel);
        break;
    case MenuSelection_Three:
        scene_manager_handle_custom_event(app->scene_manager, Event_ShowEditPanel);
        break;
    }
}
void fancy_remote_scene_on_enter_MainMenu(void* context) {
    FancyRemote* app = context;

    submenu_reset(app->submenu);
    submenu_add_item(
        app->submenu, "option #1", MenuSelection_One, fancy_remote_menu_callback_main_menu, app);
    submenu_add_item(
        app->submenu, "Use Remote", MenuSelection_Two, fancy_remote_menu_callback_main_menu, app);
    submenu_add_item(
        app->submenu,
        "Edit Remote",
        MenuSelection_Three,
        fancy_remote_menu_callback_main_menu,
        app);
    view_dispatcher_switch_to_view(app->view_dispatcher, FView_Menu);
}
bool fancy_remote_scene_on_event_MainMenu(void* context, SceneManagerEvent event) {
    bool consumed = false;
    FancyRemote* app = context;
    switch(event.type) {
    case SceneManagerEventTypeCustom:
        switch(event.event) {
        case Event_ShowPopupOne:
            scene_manager_next_scene(app->scene_manager, Scene_PopupOne);
            consumed = true;
            break;
        case Event_ShowRemotePanel:
            scene_manager_next_scene(app->scene_manager, Scene_RemotePanel);
            consumed = true;
            break;
        case Event_ShowEditPanel:
            scene_manager_next_scene(app->scene_manager, Scene_EditPanel);
            consumed = true;
            break;
        }
        break;
    default:
        consumed = false;
    }
    return consumed;
}
void fancy_remote_scene_on_exit_MainMenu(void* context) {
    FancyRemote* app = context;
    submenu_reset(app->submenu);
}
void fancy_remote_scene_on_enter_PopupOne(void* context) {
    FancyRemote* app = context;
    popup_reset(app->popup);
    popup_set_context(app->popup, app);
    popup_set_header(app->popup, "Pop#1", 60, 10, AlignCenter, AlignTop);
    popup_set_icon(app->popup, 10, 10, NULL);
    popup_set_text(app->popup, "have some text", 60, 20, AlignLeft, AlignTop);
    view_dispatcher_switch_to_view(app->view_dispatcher, FView_Popup);
}
void fancy_remote_scene_on_enter_RecordPanel(void* context) {
    FancyRemote* app = context;
    popup_reset(app->popup);
    popup_set_context(app->popup, app);
    popup_set_header(app->popup, "Add Button", 60, 10, AlignLeft, AlignTop);
    popup_set_icon(app->popup, 10, 10, NULL);
    popup_set_text(app->popup, "Point remote and press button", 5, 20, AlignLeft, AlignCenter);
    view_dispatcher_switch_to_view(app->view_dispatcher, FView_Popup);
}
